  constructor(props) {
    super(props)

    this.state = {
       items:[],
       currentItem:{
         text:'',
         key:''
       }
    }
    this.handleInput = this.handleInput.bind(this);
    this.addItem = this.addItem.bind(this);
  }

  handleInput(e) {
    this.setState({
      currentItem: {
        text: e.target.value,
        key: Date.now()
      }
    })
