import logo from './logo.svg';
import './App.css';
import { Component } from 'react';

class App extends Component {

  constructor(props) {
    super(props)

    this.state = {
       items:[],
       currentItem:{
         text:'',
         key:''
       }
    }
  }

  handleInput


  render() {
    return (
      <div className="App">
          <header>
            <form id="to-do-form">
              <input type="text" onSubmit={this.addItem} placeholder="Enter text" value={this.state.currentItem.text} onChange={this.handleInput} />
              <button type="submit">Add</button>
            </form>
          </header>
      </div>
    );
  }

}

export default App;
